import React from 'react';
import { connect } from 'react-redux';
import * as Styles from './login-style';
import { Card } from '../../components/form/form'; // input too
import { Button } from '../../components/button/buttons';
import axios from 'axios';
import './login-style';

class Login extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
            username: '',
            password: '',
            loading: false,
            submitted: false,
            errorMsg: null,
            isError: null
        }
    }

    handleChange = (event) => {
        const { name, value } = event.target;
        this.setState({
            [name]: value
        })
    }

    async handleSubmit (event) {
        event.preventDefault();

        const { username, password } = this.state;
        const userData = {
            username: username,
            password: password
        }
        console.log('userData',userData);

        this.setState({
            loading: true,
            // isError: false,
            // errorMsg: null
        });

        let userObj = await this.checkDbForUsernameAndPassword(userData);

        console.log('userObj',userObj);
        if (userObj) {
            console.log('no error');
            console.log('userObj in handleSubmit',userObj);
            // this.sendToReduxStore(userObj);

        } else {
            console.log('no userObj');
        }

    }

    sendToReduxStore = (data) => {

        this.props.dispatch({
            type: 'USER_LOGIN_REQUEST',
            payload: data
        });
        this.setState({
            submitted: true
        });


        // this.setState({
        //     submitted: true,
        //     loading: false,
        //     errorArray: [],
        //     isError: false
        // });
    }

    componentDidUpdate = () => {
        if (this.state.submitted) {
            // do stuff - take user to Home page?
            console.log('user data submitted');

            // bring user to login page
            // window.location = '/login';
        }
    }




    checkDbForUsernameAndPassword (data) {

        axios.get('/api/user/login', {
            params: data
        })
        .then(resp => {
            console.log('resp.data',resp.data);

            // make the object to return to the redux function. assume isError is true, unless it's switched to false
            let userObj = {
                user: null,
                isError: true
            };

            if (resp.status === 200) {
                console.log('success');

                this.setState({
                    loading: false
                });

                if (resp.data === null) {
                    console.log('resp.data is null');
                    this.setState({
                        errorMsg: `We couldn\'t find your profile. Please check your username and password and try again.`,
                        isError: true
                    });
                } else {
                    this.setState({
                        errorMsg: null,
                        isError: false
                    });
                    // return an object to pass into redux
                    userObj = {
                        user: resp.data,
                        isError: false
                    };
                    return userObj;
                }
            } else {
                console.log('front end /api/user/create error');
            }

            return userObj;

        }).catch(err => {
            this.setState({
                errorMsg: `We ran into an issue trying to find your account.`,
                isError: true
            });
            console.log(err);
        });


    }


    render() {
        return (
            <div>
                <Styles.Wrapper>
                    <Card>
                        <form>
                            <label>Username: </label>
                            <input
                                type="text"
                                name="username"
                                value={this.state.username}
                                onChange={event => this.handleChange(event)}
                            />
                            <br></br>
                            <label>Password: </label>
                            <input
                                type="text"
                                name="password"
                                value={this.state.password}
                                onChange={event => this.handleChange(event)}
                            />
                            <br></br>
                            <button onClick={this.handleSubmit}>submit</button>
                            {/* <Button onClick={this.handleSubmit}>submit</Button> */}
                        </form>
                    </Card>
                    {this.state.isError ? <div className='error-box'><p>{this.state.errorMsg}</p></div> : ''}
                    <p>State user: {this.state.username}</p>
                    <p>State pw: {this.state.password}</p>
                    <p>Global props user: {this.props.username}</p>
                    <p>Global props pw: {this.props.password}</p>
                </Styles.Wrapper>
            </div>
        )
    }
}

function mapStateToProps(state) {
    console.log('Login: mapStateToProps state',state);
    return {
      username: state.username,
      password: state.password,
      isLoggedIn: true
    };
}

export default connect(mapStateToProps)(Login);
