import React from 'react';
import { connect } from 'react-redux';
import './signup-style.css';

class Signup extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
            username: '',
            password: '',
            email: '',
            address: '',
            city: '',
            stateUSA: '',
            zip: '',
            submitted: false,

            error: ''
        }
    }

    handleChange = (event) => {
        console.log('signup handleChange');
        const { name, value } = event.target;
        this.setState({
            [name]: value
        });
    }

    handleSubmit = (event) => {
        event.preventDefault();

        this.setState({
            submitted: true
        }, this.sendToReduxStore);
    }

    sendToReduxStore = () => {
        const { username, password, email, address, city, stateUSA, zip } = this.state;

        const reduxData = {
            username: username,
            password: password,
            email: email,
            address: address,
            city: city,
            stateUSA: stateUSA,
            zip: zip
        }
        console.log('reduxData',reduxData);

        if (this.state.submitted) {
            // this.loginAcceptedOrDenied(reduxData);
            this.validateUserInputs(reduxData);
        }
    }

    errorMessageArray = [];

    displayErrorMessage = (array) => {
        console.log('displaying error message...');
        // for (let n=0; n < array.length; n++) {
        //     this.errorMessageArray.push(array[n]);
        // }
        // console.log('this.errorMessageArray',this.errorMessageArray);
        // let errorMessageArrayJoin = this.errorMessageArray.join('\n');

        let errorMessageArrayJoin = array.join('\n');
        console.log('errorMessageArrayJoin',errorMessageArrayJoin);
        return <h4>yoooooo</h4>;
    }

    validateUserInputs = (data) => {
        console.log('validating inputs...');
        console.log(data.username.length);
        if (data.username.length > 20 || data.username.length < 3) {
            this.errorMessageArray.push('Username must be between 3-20 characters.');
        } else {
            this.errorMessageArray.push('Username looks good!');
        }
        if (data.password.length > 20 || data.username.length < 3) {
            this.errorMessageArray.push('Username must be between 3-20 characters.');
        } else {
            this.errorMessageArray.push('Username looks good!');
        }

        console.log('this.errorMessageArray',this.errorMessageArray);

        this.displayErrorMessage(this.errorMessageArray);
    }


    loginAcceptedOrDenied = (data) => {
        // check db for login data here
        data.isLoggedIn = true;

        this.props.dispatch({
            type: 'USER_SIGNUP_REQUEST',
            payload: data
        });
        this.setState({
            submitted: false
        });

        // bring user to login page
        window.location = '/login';
    }



    render() {
        return (
            <div>
                <h2>This is the signup page.</h2>
                <h3>Please fill out the information below.</h3>
                <h4>{this.state.error}</h4>
                <form>
                    <label>Username: </label>
                    <input
                        type="text"
                        name="username"
                        value={this.state.username}
                        onChange={event => this.handleChange(event)}
                    />
                    <label>Password: </label>
                    <input
                        type="text"
                        name="password"
                        value={this.state.password}
                        onChange={event => this.handleChange(event)}
                    />
                    <label>Email: </label>
                    <input
                        type="text"
                        name="email"
                        value={this.state.email}
                        onChange={event => this.handleChange(event)}
                    />
                    <h4>Shipping Info</h4>
                    <label>Address: </label>
                    <input
                        type="text"
                        name="address"
                        value={this.state.address}
                        onChange={event => this.handleChange(event)}
                    />
                    <label>City: </label>
                    <input
                        type="text"
                        name="city"
                        value={this.state.city}
                        onChange={event => this.handleChange(event)}
                    />
                    <label>State: </label>
                    <input
                        type="text"
                        name="stateUSA"
                        value={this.state.stateUSA}
                        onChange={event => this.handleChange(event)}
                    />
                    <label>Zip: </label>
                    <input
                        type="text"
                        name="zip"
                        value={this.state.zip}
                        onChange={event => this.handleChange(event)}
                    />
                    <br></br>
                    <button onClick={this.handleSubmit}>submit</button>

                </form>
                <h4>Testing123...</h4>
                <div>Username: {this.props.username}</div>
                <div>Password: {this.props.password}</div>
                <div>Email: {this.props.email}</div>
                <div>Address: {this.props.address}</div>
                <div>City: {this.props.city}</div>
                <div>State: {this.props.stateUSA}</div>
                <div>Zip: {this.props.zip}</div>
            </div>
        )
    }
}

function mapStateToProps(state) {
    console.log('Product: mapStateToProps state',state);
    return {
        username: state.username,
        password: state.password,
        email: state.email,
        address: state.address,
        city: state.city,
        stateUSA: state.stateUSA,
        zip: state.zip
    };
}

export default connect(mapStateToProps)(Signup);
