import React from 'react';
import { connect } from 'react-redux';
import PrettyProfile from './orderHistoryStyles';
import './orderHistory-style.css';
import axios from 'axios';
import ProductListingProfile from "../../components/productListing/productListingProfile";
import { calculateCreatedAt, calculateTimeRemaining, showDurationTimeRemaining } from '../../components/timeConverter/timeConverter';

class OrderHistory extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
            userId: '',
            username: '',
            bidArray: [],
            auctionIdArray: [],
            auctionArray: [],

            loading: true,
            errorArray: [],
            isError: null
        }
    }

    componentDidMount = () => {
        this.setState({
            userId: this.props.userId,
            username: this.props.username,
            bidArray: [], // every bid the user has cast
            auctionArray: [],

            loading: true,
            errorArray: [],
            isError: null
        }, () => {
            this.pullBidsFromDb(this.state.userId);
        })
    }


    pullBidsFromDb = (userId) => {
        console.log('Pulling users bids from the DB with this ID: ',userId);
        const params = { userId };
        axios.get('/api/bid/auctionBids', { params })
        .then(resp => {
            console.log('These are all user bids: ',resp.data);
            this.setState({
                bidArray: resp.data,
                loading: false,
            }, () => {
                this.getAuctionIds(this.state.bidArray);
            })
        }).catch(err => {
            console.log(err);
        });
    }

    getAuctionIds(bidArray) {
        console.log('getting auction IDs...');
        let auctionIdArray = [];

        bidArray.map( (auction) => {
            auctionIdArray.push(auction.id);
        })

        this.setState({
            auctionIdArray: auctionIdArray
        }, () => {
          console.log('These are the IDs of the auctions: ',this.state.auctionIdArray);
        //   this.loopAuctionsForHighestBid(this.state.completedAuctionIdArray);
        })
    }

    pullAuctionFromDb = (auctionId) => {
        console.log('pullAuctionFromDb with this auctionId: ',auctionId);
        let params = { auctionId };
        axios.get('/api/auction/id', { params })
        .then(resp => {
            // resolve(resp.data);
        }).catch(err => {
            console.log(err);
        });
    }


    displayBids = (bidArray) => {
        console.log('displaying bids...',bidArray);

        const bidArrayMapped = bidArray.map( (bid) => {
            console.log('bid',bid);
            return (
                <div key={bid.id}>
                    <p>Bid Amount: ${bid.bidAmount}</p>
                    <p>Bid Submitted At: {calculateCreatedAt(bid.bidSubmitTime)}</p>
                    {/* {this.pullAuctionFromDb(bid.AuctionId)} */}
                    <p>Auction ID: {bid.AuctionId}</p>
                </div>
            )
        })
        return <div>{bidArrayMapped}</div>
    }

    loopAuctionsForHighestBid(completedAuctionIdArray) {
        console.log('Now, we\'re looping through the auction array and pulling highest bids for each one');

        const bidPromises = [];
        for (let n=0; n<completedAuctionIdArray.length; n++) {
          bidPromises.push(new Promise( (resolve, reject) => {
            this.fetchHighestBidFromDb(completedAuctionIdArray[n])
              .then(resp => {
                resolve(resp.data);
              }).catch(err => {
                reject(err);
              })
          }))
        }

        Promise.all(bidPromises).then(bidPromiseData => {
          const filteredArray = bidPromiseData.filter( (value) => {
            return value != '';
          })
          this.setState({
            winningBidsArray: filteredArray
          }, () => {
            console.log("winningBidsArray has been set")
            this.findUserAuctionWins(this.state.winningBidsArray);
          })
        }).catch(err => {
          console.log('an error occurred: ',err);
        })
      }

      // Returns a promise
      fetchHighestBidFromDb(auctionId) {
        const auctionData = {
          auctionId
        };
        return axios.get('/api/bid/completedAuctionHighestBid', {
          params: auctionData
        })
      }


    render() {
        return(
            <PrettyProfile>
                {this.state.loading ? 
                    <h3>{this.state.isError ? this.state.errorMsg : 'Loading...'}</h3>
                : (
                <div>
                    <h2>My Order History</h2>

                    {this.state.bidArray.length > 0 ? <div>{this.displayBids(this.state.bidArray)}</div> : <p>No bids to show.</p>}

                </div>)
                }
            </PrettyProfile>
        )
    }
}

function mapStateToProps(state) {
    console.log('OrderHistory: mapStateToProps state',state);
    return {
      username: state.username,
      userId: state.userId,
      isLoggedIn: true
    };
}

export default connect(mapStateToProps)(OrderHistory);
