import React from 'react';
import { Link } from "react-router-dom";
import Product from '../../pages/product/product';

class ProductListing extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      test: 'test'
    }
  }

  showTime = (time) => {
    let days = time.days();
    let hours = time.hours();
    let minutes = time.minutes();
    if (days < 1) {
      days = '';
    } else if (days === 1) {
      days = days.toString() + ' day, '
    } else {
      days = days.toString() + ' days, '
    }
    if (hours < 1) {
      hours = '';
    } else if (hours === 1) {
      hours = hours.toString() + ' hour, '
    } else {
      hours = hours.toString() + ' hours, '
    }
    return <h1>{days}{hours}</h1>
  }

  render() {
    console.log('this.props.durationTimeRemaining',this.props.durationTimeRemaining);
    return (
      <div>
        <Link to={`/product/${this.props.auctionId}`} component={Product}>
          <img src={this.props.imgLink} height='150px' width='150px' alt='Image...' />
        </Link>
        <Link to={`/product/${this.props.auctionId}`} component={Product}>
          <h3>Title: {this.props.title}</h3>
        </Link>

        <p>Description: {this.props.description}</p>
        <p>Gender: {this.props.gender}</p>
        <p>Category: {this.props.category}</p>
        <p>Starting Price: {this.props.startingPrice}</p>
        <p>Minimum Bid Increment: {this.props.minBidIncrement}</p>
        {/* <p>Date Posted: {this.props.createdAt}</p>
        <p>Auction End Date: {this.props.createdAtTest}</p>
        <p>Time Remaining: {this.props.createdAtTest2}</p> */}
        {/* <p>Time Remaining: {this.props.durationTimeRemaining.days() <= 1 ? 'day' : '>1'} days, {this.props.durationTimeRemaining.hours()} hours, {this.props.durationTimeRemaining.minutes()} minutes</p> */}
        <p>Time left: {this.showTime(this.props.durationTimeRemaining)}</p>

      </div>
    )
  }
}

export default ProductListing;
