import styled from 'styled-components';

const StyleInput = styled('div')`
    background-color: lightgrey;
    padding: 3rem;
    width: 30%;
    max-width: 15rem;
    margin: 10vh auto;
    text-align: center;
    border: 2px solid grey;
    border-radius: 3px;
    border-style: solid;
    border-width: 3px;
`;

export default StyleInput;
