import React from 'react';
import { Link } from "react-router-dom";
import PrettyNav from './navStyles';
import { connect } from 'react-redux';
import { HomeNav } from './HomeNav';
import { ProfileNav } from './ProfileNav';

class Nav extends React.Component {
    handleLogOut = () => {
        this.props.dispatch({
            type: 'USER_LOGOUT_REQUEST'
            // no payload to send!
        });
    }

    displayNavLinks = () => {
        if (this.props.username !== '') {
            return (
                <ProfileNav>
                   <Link className='logo' to='/'>:shirt: Thread Auction</Link>
                   <Link className='create' to='/create-auction'>Create Auction</Link>
                   <Link className='welcome' to='/profile'>Welcome {this.props.username}</Link>
                   <Link className='logout' to='/' onClick={this.handleLogOut}>Log Out</Link>
               </ProfileNav>
           )
       } else if (this.props.username === '') {
           return (
               <HomeNav>
                   <Link className='logo' to='/'>:shirt: Thread Auction</Link>
                   <Link className='login' to='/login' className='login'>Login</Link>
                   <Link className='signup' to='/signup' className='signup'>Signup</Link>
               </HomeNav>
            )
        } else {
            return (
                <div>
                    <span>something went wrong</span>
                </div>
            )
        }
    }

    render() {
        return (
            <div>
                {this.displayNavLinks()}
            </div>
        )
    }
}

function mapStateToProps(state) {
    console.log('Product: mapStateToProps state',state);
    return {
      username: state.username,
      isLoggedIn: true
    };
}

export default connect(mapStateToProps)(Nav);
