import React from 'react';
import axios from 'axios';
import { calculateCreatedAt, calculateTimeRemaining, showDurationTimeRemaining } from '../timeConverter/timeConverter';

class BidStatus extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      winningBid: null,
      bidResult: null,
    }
  }

  // is the auction complete
  // is the users bid the max amount

  // we'll need--
  // auction createdAt or boolean whether or not it's complete
  // userID
  // user bid amount


  getIsAuctionComplete = (createdAt) => {
    const momentTimeRemaining = calculateTimeRemaining(createdAt);
    const durationTimeRemainingObj = showDurationTimeRemaining(momentTimeRemaining);
    const isAuctionComplete = durationTimeRemainingObj.isComplete
    return isAuctionComplete
  }



  async isUserBidHighestBid(auctionId,userBid) {
    const winningBid = await this.getMaxBidAmountFromAuctionId(auctionId);
    console.log('winningBid: ',winningBid, ' vs user bid: ',userBid);
    this.setState({
      winningBid: winningBid
    }, () => {
      let bidResult = null;
      if (winningBid === userBid) {
        bidResult = 'Auction won';
      } else {
        bidResult = 'Not the highest bid';
      }
      this.setState({
        bidResult: bidResult
      })
    })
  }

  // returns a promise
  getMaxBidAmountFromAuctionId (auctionId) {
    const params = { auctionId };
    const bidAmountResult = axios.get('/api/bid/completedAuctionHighestBid', { params })
    .then(resp => {
        return resp.data.bidAmount
    }).catch(err => {
        console.log(err);
    });
    return bidAmountResult
  }


  showBidStatus() {
    const isAuctionComplete = this.getIsAuctionComplete(this.props.createdAt);
    this.isUserBidHighestBid(this.props.auctionId,this.props.bidAmount);

    console.log('This is the bidStatus component state',this.state);
    return (
      <div>
        <span>{isAuctionComplete ? 'Complete' : 'Ongoing'}</span>
        {/* <span>{bidResult}</span> */}
      </div>
    )
  }

  render() {
    console.log('this.props',this.props);
    return (
      <div>
        {this.showBidStatus()}
      </div>
    )
  }
}

export default BidStatus;





