import React from 'react';
import { connect } from 'react-redux';
import PrettyProfile from './profileStyles';
import './userProfile-style.css';

class UserProfile extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
            username: '',
            password: '',
            loading: false,
            submitted: false,
            errorMsg: null,
            isError: null,
            sendToRedux: false,
            sendToReduxData: null,

            // submitted: false,
            loading: false,
            // errorArray: [],
            // isError: null
        }
    }

    componentDidMount = () => {
        this.pullUserDataFromDb(this.props.userId);
        this.setState({
            loading: false
        })
    }

    pullUserDataFromDb = (userId) => {
        console.log(userId);

        const userData = {
            userId: userId
        };

        axios.get('/api/user/login', {
            params: userData
        })
        .then(resp => {
            console.log('resp.data',resp.data);

            if (resp.status === 200) {
                console.log('success');

                this.setState({
                    loading: false
                });

                if (resp.data === null) {
                    console.log('resp.data is null');
                    this.setState({
                        errorMsg: `We couldn\'t find your profile. Please check your username and password and try again.`,
                        isError: true
                    });
                } else {
                    // return an object to pass into redux
                    this.setState({
                        errorMsg: null,
                        isError: false,
                        sendToRedux: true,
                        sendToReduxData: resp.data
                    });
                    return
                }
            } else {
                console.log('front end /api/user/create error');
            }

        }).catch(err => {
            this.setState({
                errorMsg: `We ran into an issue trying to find your account.`,
                isError: true
            });
            console.log(err);
        });
    }



    render(){
        return(
            <PrettyProfile>
                <h1>{this.state.loading ? 'Loading...' : 'Profile Settings'}</h1>
                <p>Username: {this.state.username}</p>
                <p>First name: {this.state.firstName}</p>
                <p>Last name: {this.state.lastName}</p>
                <p>Username: {this.state.username}</p>
                <p>Password: {this.state.password}</p>
                <p>Email: {this.state.email}</p>
                <p>Address: {this.state.address}</p>
                <p>City: {this.state.city}</p>
                <p>State: {this.state.stateUSA}</p>
                <p>Zip: {this.state.zip}</p>
            </PrettyProfile>
        )
    }
}

function mapStateToProps(state) {
    console.log('UserProfile: mapStateToProps state',state);
    return {
      username: state.username,
      userId: state.userId,
      isLoggedIn: true
    };
}

export default connect(mapStateToProps)(UserProfile);
