import React from 'react';
import { connect } from 'react-redux';
import * as Styles from './login-style';
import { Card } from '../../components/form/form'; // input too
import { Button } from '../../components/button/buttons';
import axios from 'axios';

class Login extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
            username: '',
            password: '',
            loading: false,
            submitted: false,
            dbData: []
        }
    }

    handleChange = (event) => {
        const { name, value } = event.target;
        this.setState({
            [name]: value
        })
    }

    handleSubmit = (event) => {
        event.preventDefault();

        this.setState({
            loading: true
        }, this.sendToReduxStore);
    }

    sendToReduxStore = () => {
        const { username, password } = this.state;

        const userData = {
            username: username,
            password: password
        }
        console.log('userData',userData);

        if (this.state.submitted) {
            this.loginAcceptedOrDenied(userData);
        }
    }

    loginAcceptedOrDenied = (data) => {
        // check db for login data here
        data.isLoggedIn = true;

        this.props.dispatch({
            type: 'USER_LOGIN_REQUEST',
            payload: data
        });
        this.setState({
            submitted: false
        });
    }

    checkDbForUsernameAndPassword = (data) => {
        axios.get('/api/user/login', data)
            .then(resp => {
                console.log(resp.data);
                if (resp.status === 200) {
                    console.log('success');
                } else {
                    console.log('front end /api/user/create error');
                }
                // this.setState({
                //     submitted: true,
                //     loading: false,
                //     errorArray: [],
                //     isError: false
                // });
            }).catch(err => {
                alert('problem looking for username and pw in db');
            })
    }


    render() {
        return (
            <div>
                <Styles.Wrapper>
                    <Card>
                        <form>
                            <label for="">Username: </label>
                            <input
                                type="text"
                                name="username"
                                value={this.state.username}
                                onChange={event => this.handleChange(event)}
                            />
                            <br></br>
                            <label for="">Password: </label>
                            <input
                                type="text"
                                name="password"
                                value={this.state.password}
                                onChange={event => this.handleChange(event)}
                            />
                            <br></br>
                            <button onClick={this.handleSubmit}>submit</button>
                            {/* <Button onClick={this.handleSubmit}>submit</Button> */}
                        </form>
                    </Card>
                    <p>State user: {this.state.username}</p>
                    <p>State pw: {this.state.password}</p>
                    <p>Global props user: {this.props.username}</p>
                    <p>Global props pw: {this.props.password}</p>
                </Styles.Wrapper>
            </div>
        )
    }
}

function mapStateToProps(state) {
    console.log('Login: mapStateToProps state',state);
    return {
      username: state.username,
      password: state.password,
      isLoggedIn: true
    };
}

export default connect(mapStateToProps)(Login);
