import styled from 'styled-components';

const SignupForm = styled("div")`
    text-align: center;

    input {
        width: 40%;
        padding: 10px;
        margin: 20px;
        @media(max-width: 700px){
            width: 80%;
            font-size: .5rem;
        }
    }
    .userInfo {
        display: flex;
        margin-top: 30px;
        flex-direction: column;
    }
    .shippingInfo {
        display: flex;
        flex-direction: column;
    }
    .submitBtn {
        width: auto;
        font-size: 2rem;
        border-radius: 5px;
        margin-bottom: 50px;
    }
    .submitBtn:hover {
        background-color: rgb(99, 180,245);
        color: white;
    }

`;

export default SignupForm;
