export const validateInputs = (data) => {

  if (data.username.length > 20 || data.username.length < 3) {
    this.setState({
      errorArray: 'Username must be between 3-20 characters.'
    });
  } else {
    this.setState({
      errorArray: 'Username looks good!'
    });
  }
}
