  //   .then(resp => {
  //       console.log('This should be the max amount: ',resp.data.bidAmount);
  //       return resp.data.bidAmount
  //   }).catch(err => {
  //       console.log(err);
  //   });
  //   return bidAmountResult
  // }

  showBidStatus() {
    const isAuctionComplete = this.getIsAuctionComplete(this.props.createdAt);
    this.isUserBidWinningBid()
    return <span>{isAuctionComplete ? 'complete' : 'not complete'}</span>
  }

  render() {
    console.log('this.props',this.props);
    return (
      <div>
        <p>{this.showBidStatus()}</p>
      </div>
    )
  }
}

export default BidStatus;




