import React, { Component } from "react";
import * as Styled from './homeStyles';
import Product from '../product/product';
import { Link } from "react-router-dom";
import ProductListing from '../../components/productListing/productListing';
import axios from 'axios';

class Home extends Component {
    constructor(props) {
        super(props)

        this.state = {
            auctionId: 1,
            auctionId2: 2,

            productObjectArray: [],

            // title: '',
            // description: '',
            // gender: '',
            // category: '',
            // startingPrice: '',
            // minBidIncrement: '',

            // submitted: false,
            // loading: false,
            errorArray: [],
            isError: null
        }
    }


    componentDidMount = () => {
        // this.showProducts();
        const productsPulled = this.pullProductDataFromDb();
        setTimeout( () => {
            console.log('productsPulled',productsPulled);
        }, 2000 );
    }

    pullProductDataFromDb = () => {
        // will need params later for filtering


        // const auctionData = {
        //     auctionId: auctionId
        // };

        axios.get('/api/auction/all', {
            // params: auctionData
        })
        .then(resp => {
            console.log('resp.data',resp.data);

            if (resp.status === 200) {
                console.log('success');

                this.setState({
                    productObjectArray: resp.data
                }, () => {
                    console.log('productObjArray',this.state.productObjectArray);
                    return true;
                });


                if (resp.data === null) {
                    console.log('resp.data is null');
                    this.setState({
                        errorMsg: `Error loading products. Please reload the page.`,
                        isError: true
                    });
                } else {
                    this.setState({
                        errorMsg: null,
                        isError: false
                    });
                    return
                }
            } else {
                console.log('front end /api/auction/all error');
            }

        }).catch(err => {
            this.setState({
                errorMsg: `Error loading products. Please reload the page.`,
                isError: true
            });
            console.log(err);
        });
    }

    showProducts = () => {
        const numArray = [1, 2, 3];
        const numArrayMapped = numArray.map( (num) => {
            return (
                <Styled.PlainBox>
                    <ProductListing
                        key={num}
                        auctionId={num}
                        imgLink='https://diy.sndimg.com/content/dam/images/diy/fullset/2011/7/26/1/iStock-10761186_halloween-pumpkin-in-garden_s4x3.jpg.rend.hgtvcom.966.725.suffix/1420851319631.jpeg'
                        title='My title'
                        description='my descrip'
                        gender='my gend'
                        category='my cat'
                        startingPrice='my startingPrice'
                        minBidIncrement='my min bid'
                    />
                </Styled.PlainBox>
            )
        })
        return <div>{numArrayMapped}</div>
    }

    render() {
        return (
            <Styled.BootstrapContainer>
                <Link className='logo' to={`/product/${this.state.auctionId2}`} component={Product}>/product/:auctionId 2</Link>
                {this.showProducts()}
            </Styled.BootstrapContainer>
        )
    }
}

export default Home;
