import React from 'react';
import { connect } from 'react-redux';
import axios from 'axios';
import * as Styles from './product-style';
import { Card } from '../../components/form/form'; // input too


class Product extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
            title: '',
            description: '',
            gender: '',
            category: '',
            startingPrice: '',
            minBidIncrement: '',

            submitted: false,
            loading: false,
            errorArray: [],
            isError: null
        }
    }

    componentDidMount = () => {
        const auctionId = 1;
        // this.pullProductDataFromDb(auctionId);
        this.setState({
            loading: true,
            errorArray: [],
            isError: null
        })
    }

    pullProductDataFromDb = (auctionId) => {
        console.log('auctionId',auctionId);

        const auctionData = {
            auctionId: auctionId
        };

        axios.get('/api/auction/id', {
            params: auctionData
        })
        .then(resp => {
            console.log('resp.data',resp.data);

            if (resp.status === 200) {
                console.log('success');

                this.setState({
                    firstName: resp.data.firstName,
                    lastName: resp.data.lastName,
                    username: resp.data.username,
                    password: resp.data.password,
                    email: resp.data.email,
                    address: resp.data.address,
                    city: resp.data.city,
                    stateUSA: resp.data.stateUSA,
                    zip: resp.data.zip,
                    loading: false
                });

                if (resp.data === null) {
                    console.log('resp.data is null');
                    this.setState({
                        errorMsg: `We couldn\'t find your profile. Please reload the page.`,
                        isError: true
                    });
                } else {
                    // return an object to pass into redux
                    this.setState({
                        errorMsg: null,
                        isError: false
                    });
                    return
                }
            } else {
                console.log('front end /api/user/profile error');
            }

        }).catch(err => {
            this.setState({
                errorMsg: `We ran into an issue trying to find your account. Please reload the page.`,
                isError: true
            });
            console.log(err);
        });
    }



    render(){
        return(
            <Styles.Wrapper>
                {this.state.loading ? 
                    <h3>{this.state.isError ? this.state.errorMsg : 'Loading...'}</h3>
                : (
                <div>
                    <h1>My Profile</h1>
                    <p>Username: {this.state.username}</p>
                    <p>First name: {this.state.firstName}</p>
                    <p>Last name: {this.state.lastName}</p>
                    <p>Username: {this.state.username}</p>
                    <p>Password: {this.state.password}</p>
                    <p>Email: {this.state.email}</p>
                    <p>Address: {this.state.address}</p>
                    <p>City: {this.state.city}</p>
                    <p>State: {this.state.stateUSA}</p>
                    <p>Zip: {this.state.zip}</p>
                </div>)
                }
            </Styles.Wrapper>
        )
    }
}

function mapStateToProps(state) {
    console.log('UserProfile: mapStateToProps state',state);
    return {
      username: state.username,
      userId: state.userId,
      isLoggedIn: true
    };
}

export default connect(mapStateToProps)(Product);
