import React from 'react';
import { connect } from 'react-redux';
import PrettyProfile from './orderHistoryStyles';
import './orderHistory-style.css';
import axios from 'axios';
import ProductListingProfile from "../../components/productListing/productListingProfile";


class OrderHistory extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
            userId: '',
            username: '',
            auctionArray: [],

            loading: true,
            errorArray: [],
            isError: null
        }
    }

    componentDidMount = () => {
        this.setState({
            userId: this.props.userId,
            username: this.props.username,
            loading: true,
            errorArray: [],
            isError: null
        }, () => {
            this.pullAuctionsFromDb(this.state.userId);
        })
    }


    pullAuctionsFromDb = (userId) => {
        console.log('userId',userId);

        let userParams = {
            userId: userId
        };

        console.log('userParams',userParams);

        axios.get('/api/user/auctions', {
            params: userParams
        })
        .then(resp => {
            console.log('resp.data',resp.data);

            if (resp.status === 200) {
                console.log('success');

                this.setState({
                    auctionArray: resp.data
                }, () => {
                    console.log('auction array state',this.state.auctionArray)
                    this.displayAuctions(this.state.auctionArray);
                })

                if (resp.data === null) {
                    console.log('resp.data is null');
                    this.setState({
                        errorMsg: `We couldn\'t find your profile. Please reload the page.`,
                        isError: true
                    });
                } else {
                    // return an object to pass into redux
                    this.setState({
                        errorMsg: null,
                        isError: false
                    });
                    return
                }
            } else {
                console.log('front end /api/user/profile error');
            }

        }).catch(err => {
            this.setState({
                errorMsg: `We ran into an issue trying to find your account. Please reload the page.`,
                isError: true
            });
            console.log(err);
        });
    }

    displayAuctions = (auctionArray) => {
        console.log('displaying auctions...',auctionArray);

        const auctionArrayMapped = this.state.auctionArray.map( (auction) => {
            console.log('auction',auction);
            return (
                <ProductListingProfile
                    key={auction}
                    auctionId={auction.id}
                    imgLink={auction.imgLink}
                    title={auction.title}
                    startingPrice={auction.startingPrice}
                    createdAt={auction.createdAt}
                />
            )
        })

        return <div>{auctionArrayMapped}</div>
    }

    displayBids = (bidArray) => {
        console.log('displaying bids...',bidArray);

        const testArray = [1,2,3,4,5];
        const bidArrayMapped = testArray.map( (test) => {
            console.log('bid',test);
            return (
                <p key={test}>{test}</p>
            )
        })

        return <div>{bidArrayMapped}</div>
    }


    render() {
        return(
            <PrettyProfile>
                {this.state.loading ? 
                    <h3>{this.state.isError ? this.state.errorMsg : 'Loading...'}</h3>
                : (
                <div>
                    <h2>My Order History</h2>

                    {this.state.auctionArray.length > 0 ? <div>{this.displayAuctions()}</div> : <p>No orders to show.</p>}
                    {this.displayBids()}

                </div>)
                }
            </PrettyProfile>
        )
    }
}

function mapStateToProps(state) {
    console.log('OrderHistory: mapStateToProps state',state);
    return {
      username: state.username,
      userId: state.userId,
      isLoggedIn: true
    };
}

export default connect(mapStateToProps)(OrderHistory);
